import React,{useState} from 'react';
import {  makeStyles } from '@material-ui/core/styles';
import {Button,Toolbar,Typography, Grid, CardActions,Card,CardMedia, CardContent} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  card: {
      boxShadow: "none",
  },
  t_text: {
      fontSize: "12px",
      textAlign: "center"
  },
  p_text: {
      fontSize: "15px",
      textAlign: "center"
  },
  image: {
      width: "100%",
      height: "300px"
  }
}));


export default function CarD(props) {
  const classes = useStyles();
  const [total, setTotal] = useState(0)

  const increase = () => {
    setTotal(total + 1)
  }

  const decrease = () => {
    setTotal(total - 1)
  }
  
  return (
      <React.Fragment> 
        <Grid item xs={3} >
            <Card className={classes.card} key={props.key}>
                <CardMedia component="img" image={props.image} className={classes.image}></CardMedia>
                <CardContent>
                    <Typography className={classes.t_text}>{props.title}</Typography>
                    <Typography className={classes.p_text}>{props.price}</Typography>
                </CardContent>
                <CardActions>
                    <Toolbar style={{marginLeft: "5%"}}>
                        <Button size="small" variant="contained" color="primary" on ={() => increase} >+</Button>
                        <Typography className={classes.p_text} style={{marginLeft:"10px"}}>{total}</Typography>
                        <Button size="small" variant="contained" color="secondary" style={{marginLeft:"10px"}} onClick ={() => decrease} >-</Button>
                    </Toolbar>
                </CardActions>
            </Card>
        </Grid>
    </React.Fragment>
  );
}
