import React,{useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {Toolbar,IconButton,Typography,InputBase,Badge,Button,Box,Divider, Grid,} from '@material-ui/core';
import p1 from '../image/p1.jpg';


const useStyles = makeStyles((theme) => ({
  grow: {
    flexGrow: 1,
  },
  ban1: {
      width: "100%",
      height: "600px",
  }
}));


export default function View() {
  const classes = useStyles();
  const [total, setTotal] = useState(1)

  const increase = () => {
    setTotal(total + 1)
  }

  const decrease = () => {
    setTotal(total - 1)
  }
  
  return (
      <React.Fragment>
    <div className={classes.grow}>
    <Grid container spacing={2} style={{width:"70%",marginLeft:"15%"}}>
        <Grid item xs={6}>
            <img src={p1} className={classes.ban1}  />
        </Grid>
        <Grid item xs={4}>
            <Typography >
                <Box fontSize={25} fontWeight="bold" marginTop="100px">Fortune Soya Health Refined Soyabean Oil (Pouch)</Box>
            </Typography>
            <Typography>
                <Box fontSize={25} marginTop="30px">â‚¹116.80</Box>
            </Typography>
            <Toolbar >
                <Button size="small" variant="contained" color="primary" onClick ={() => increase()} >+</Button>
                <Typography  style={{marginLeft:"10px"}}>{total}</Typography>
                <Button size="small" variant="contained" color="secondary" style={{marginLeft:"10px"}} onClick ={() => decrease()} >-</Button>
            </Toolbar>
            <Button size="small" variant="contained" color="dark">ADD TO CART</Button>
        </Grid>
    </Grid>
    </div>
    </React.Fragment>
  );
}
