import React, { useState } from 'react';
import {Grid,List, ListItem, ListItemText, Toolbar} from '@material-ui/core';
import ListShop from './ListShop';
import ProShop from './ProShop';
import {makeStyles} from '@material-ui/core/styles';
import {useParams,Link} from 'react-router-dom';

import p1 from '../image/p1.jpg';
import p2 from '../image/p2.jpg';
import p3 from '../image/p3.jpg';
import p4 from '../image/p4.jpg';

const card = [
    {
        id: 1,
        title: "Fortune Soya Health Refined Soyabean Oil (Pouch)",
        image: p1,
        price: "₹116.80",
        qty: 5,
        category: "Oil"
    },
    {
        id: 2,
        title: "Dhara Mustard Oil, 1 Lt (Pouch)",
        image: p2,
        price: "₹134.50",
        qty: 8,
        category: "Oil"
    },
    {
        id: 3,
        title: "Everest Turmeric Powder/Haldi",
        image: p3,
        price: "₹26-₹52",
        qty: 10,
        category: "Masala"
    },
    {
        id: 4,
        title: "Red Masoor Dal",
        image: p4,
        price: "₹71.80",
        qty: 15,
        category: "Dal"
    },

]

function Shop(){
    const [list_cat, ListCat] = useState(card);
    let {category} = useParams();
    const pro = card;
    return(
        <React.Fragment>
            <Grid container>
                <Grid item xs={2}>
                <List component="nav">
                        <ListItem>
                            <ListItemText>
                                <Link style={{textDecoration: "none",fontSize:15,color: "skyblue"}} to="/shop">All</Link>
                            </ListItemText>
                        </ListItem>
                    </List>
                    {list_cat.map((l,key) => (
                        <ListShop category={l.category} />
                    ))}
                </Grid>
                <Grid item xs={10}>
                    <Grid container> 
                    {pro.map((p, key)=>(
                            <Grid item xs={4}>
                                <ProShop image={p.image} title={p.title} price={p.price} category={p.category}/>
                            </Grid>
                        ))}
                    </Grid>
                </Grid>
            </Grid>
        </React.Fragment>
    )
}

export default Shop
