import React, { useState } from 'react';
import {Grid, Toolbar} from '@material-ui/core';
import ListShop from './ListShop';
import ProShop from './ProShop';
import {makeStyles} from '@material-ui/core/styles';
import {useParams} from 'react-router-dom';

import p1 from '../image/p1.jpg';
import p2 from '../image/p2.jpg';
import p3 from '../image/p3.jpg';
import p4 from '../image/p4.jpg';

const card = [
    {
        id: 1,
        title: "Fortune Soya Health Refined Soyabean Oil (Pouch)",
        image: p1,
        price: "₹116.80",
        qty: 5,
        category: "Oil"
    },
    {
        id: 2,
        title: "Dhara Mustard Oil, 1 Lt (Pouch)",
        image: p2,
        price: "₹134.50",
        qty: 8,
        category: "Oil"
    },
    {
        id: 3,
        title: "Everest Turmeric Powder/Haldi",
        image: p3,
        price: "₹26-₹52",
        qty: 10,
        category: "Masala"
    },
    {
        id: 4,
        title: "Red Masoor Dal",
        image: p4,
        price: "₹71.80",
        qty: 15,
        category: "Dal"
    },

]

function Filter(){
    const [list_cat, List] = useState(card);
    let {category} = useParams();
    const filter = card.filter(p =>  p.category == category);
    return(
        <React.Fragment>
            <Grid container>
                <Grid item xs={2}>
                    {list_cat.map((l,key) => (
                        <ListShop category={l.category} />
                    ))}
                </Grid>
                <Grid item xs={10}>
                    <Grid container> 
                        {filter.map((p, key)=>(
                            <Grid item xs={6}>
                                <ProShop image={p.image} title={p.title} price={p.title} category={p.category}/>
                            </Grid>
                        ))}
                    </Grid>
                </Grid>
            </Grid>
        </React.Fragment>
    )
}

export default Filter
